import { useState } from 'react';


export default function FeaturedProject7({ projectsProp }) {
    // Haha just ask your tutor! 
    const [projectHover, setProjectHover] = useState(false);

    const imageBackground = 'mx-auto h-28 md:h-44 bg-cover bg-center bg-fitness-tracker bg-no-repeat border-2 border-blue transform -translate-x-2 -translate-y-2 md:-translate-x-2 md:-translate-y-3';

    const replaceImageBackground = 'mx-auto h-28 md:h-44 bg-blue border-2 border-blue transform -translate-x-2 -translate-y-2 md:-translate-x-2 md:-translate-y-3';

    return (     
    <>
        <div className='w-5/12'>        
            <div
                onMouseEnter={() => setProjectHover(true)} 
                onMouseLeave={() => setProjectHover(false)}
                className={projectHover ? 'bg-bright border-2 border-blue ml-5' 
                : 'bg-blue border-2 border-bright ml-5'}>

                    <article className={projectHover ? replaceImageBackground : imageBackground}>

                        {projectHover ?(
                            <>
                                <div id='info1' className='font-display mt-4'>
                                    <h4 id='project-title' className='text-white text-md md:text-3xl text-center'>    
                                        {projectsProp.project7.name}
                                    </h4>
                                    <ul id='project-links' className='pt-2 content-center text-white text-center'>
                                        <li><a href={projectsProp.project7.deployed} className='hover:pb-1 hover:underline hover:text-bright'>Live Page</a></li>
                                        <li><a href={projectsProp.project7.repo} className='hover:pb-1 hover:underline hover:text-bright'>Visit the Repo</a></li>
                                    </ul>
                                </div>
                            </>
                        )
                        : <></>
                        }
                    </article>
            </div>
        </div>
        <div id='project-info' className='flex flex-col w-1/2'>
            <h4 id='project-title' className='text-md md:text-3xl font-display text-left'>    
                {projectsProp.project7.name}
            </h4>
            <ul className='mt-6'>
                {projectsProp.project7.tech.map((techEl, index) => 
                    <li key={index} className='font-body text-xs italic'>
                        {techEl}
                    </li>
                )}
            </ul>

        </div>
    </>
    )
};
